import { useCallback, useEffect, useState } from "react";
import { apiFetch } from "../api/client";

const TYPE_OPTIONS = [
  { value: "", label: "전체" },
  { value: "CHARGE", label: "충전" },
  { value: "USE", label: "사용" },
  { value: "REFUND", label: "환불" },
  { value: "SIGNUP_BONUS", label: "가입 보너스" },
];

function formatDelta(draftType, amount) {
    if (amount == null) return "-";

    const signed = draftType === "USE" ? -Math.abs(amount) : Math.abs(amount);
    const prefix = signed > 0 ? "+" : "";
    return `${prefix}${signed.toLocaleString()} 코인`;
}

function CoinHistoryPage() {
    const [items, setItems] = useState([]);
    const [draftType, setDraftType] = useState("");
    const [draftFrom, setDraftFrom] = useState("");
    const [draftTo, setDraftTo] = useState("");
    const [query, setQuery] = useState({ type: "", from: "", to: ""});
    const [page, setPage] = useState(0);
    const [size] = useState(20);
    const [totalPages, setTotalPages] = useState(0);
    const [loading, setLoading] = useState(false);
    const [buttonOn, setButtonOn] = useState(false);

    const loadData = useCallback ( async (pageToLoad = 0) => {
        setLoading(true);
        try {
            const params = new URLSearchParams();
            params.set("page", pageToLoad);
            params.set("size", size);
            if (query.type) params.set("type", query.type);
            if (query.from) params.set("from", query.from);
            if (query.to) params.set("to", query.to);

            const data = await apiFetch(`/api/wallet/ledger?` + params.toString(), {
                method: "GET",
            });

            setItems(data.items || []);
            setPage(data.page ?? 0);

            const totalCount = data.totalCount ?? 0;
            const pageSize = data.size ?? size;
            const pages = pageSize > 0 ? Math.ceil(totalCount / pageSize) : 1;
            setTotalPages(pages);
        } finally {
            setLoading(false);
        }
    }, [query, size]);

    useEffect(() => {
        loadData(0);
    }, [loadData]);

    const handleSearch = () => {
        setQuery({ type: draftType, from: draftFrom, to: draftTo });
        setButtonOn(true);
    };

    const handlePageChange =(newPage) => {
        if (newPage < 0 || newPage >= totalPages) return;
        loadData(newPage);
    };

    const downloadLedger = async (format) => {
        try {
            const params = new URLSearchParams();
            if (query.type) params.set("type", query.type);
            if (query.from) params.set("from", query.from);
            if (query.to) params.set("to", query.to);
            params.set("format", format); // EXCEL or PDF

            const url = `http://localhost:8080/api/wallet/ledger/export?${params.toString()}`;

            const res = await fetch(url, {
                method: "GET",
                headers: {
                    Authorization: `Bearer ${localStorage.getItem("token")}`,
                },
            });

            if (!res.ok) {
                alert("정산 내역 다운로드 중 오류가 발생했습니다.");
                return;
            }

            const blob = await res.blob();
            const downloadUrl = window.URL.createObjectURL(blob);
            const a = document.createElement("a");
            a.href = downloadUrl;
            a.download = format === "PDF" ? "coin-ledger.pdf" : "coin-ledger.xlsx";
            document.body.appendChild(a);
            a.click();
            a.remove();
            window.URL.revokeObjectURL(downloadUrl);
        } catch (err) {
            console.error(err);
            alert("정산 내역 다운로드에 실패했습니다.");
        }
    };

    return (
        <div className="container">
            <h2>코인 사용 내역</h2>
            <br />
            <div className="filters">
                <label>
                    유형:
                    <select value={draftType} onChange={(e) => setDraftType(e.target.value)}>
                        {TYPE_OPTIONS.map((opt) => (
                            <option key={opt.value} value={opt.value}>{opt.label}</option>
                        ))}
                    </select>  
                </label>
                <label>
                    시작일:
                    <input 
                        type="date"
                        value={draftFrom}
                        onChange={(e) => setDraftFrom(e.target.value)}
                    />
                </label>
                <label>
                    종료일:
                    <input 
                        type="date"
                        value={draftTo}
                        onChange={(e) => setDraftTo(e.target.value)}
                    />
                </label>
                <button className="btn-primary" onClick={handleSearch} disabled={loading}>
                    검색
                </button>
            </div>
            <br />

            {loading ? (
                <p>불러오는 중...</p>
            ) : items.length === 0? (
                <p>코인 사용 내역이 없습니다.</p>
            ) : buttonOn && (
                <table className="coin-table">
                    <thead>
                        <tr>
                            <th>일시</th>
                            <th>유형</th>
                            <th>코인 변화</th>
                            <th>잔액</th>
                            <th>설명</th>
                        </tr>
                    </thead>
                    <tbody>
                        {items.map((row) => (
                            <tr key={row.id}>
                                <td>{row.createdAt ? row.createdAt.replace("T", " ") : "-"}</td>
                                <td>{row.type}</td>
                                <td>{formatDelta(row.type, row.amount)}</td>
                                <td>{(row.balanceAfter ?? 0).toLocaleString()}</td>
                                <td>{row.description}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}

            <br />

            <div className="button-group">
                <button className="btn-secondary" onClick={() => downloadLedger("EXCEL")}>
                    엑셀로 다운로드
                </button>
                <button className="btn-secondary"
                    onClick={() => downloadLedger("PDF")} 
                    style={{ marginLeft: "8px" }}
                >
                    PDF로 다운로드
                </button>
            </div>

            {totalPages > 1 && (
                <div className="button-group pagination">
                    <button className="btn-ghost" onClick={() => handlePageChange(page -1)} disabled={page <= 0}>
                        이전
                    </button>
                    <span>{page + 1} / {totalPages}</span>
                    <button className="btn-ghost" onClick={() => handlePageChange(page + 1)} disabled={page >= totalPages -1}>
                        다음
                    </button>
                </div>
            )}
        </div>
    );
}

export default CoinHistoryPage;
